        return new Source("{% extends 'base.html.twig' %}

{% block title %}Client index{% endblock %}

{% block body %}
    <h1>Client index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>CIN</th>
                <th>Nom</th>
                <th>Prenom</th>
                <th>Image</th>
                <th>Numero</th>
                <th>Email</th>
                <th>Adresse</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for client in clients %}
            <tr>
                <td>{{ client.id }}</td>
                <td>{{ client.CIN }}</td>
                <td>{{ client.Nom }}</td>
                <td>{{ client.Prenom }}</td>
                <td>{{ client.Image }}</td>
                <td>{{ client.Numero }}</td>
                <td>{{ client.Email }}</td>
                <td>{{ client.Adresse }}</td>
                <td>
                    <a href=\"{{ path('client_show', {'id': client.id}) }}\">show</a>
                    <a href=\"{{ path('client_edit', {'id': client.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"9\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('client_new') }}\">Create new</a>
{% endblock %}
